package com.lulu.operators;

public class SimpleInterest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float SI;
		int N=20;
		float R=8.5f;
		float P=600000;
		
		SI=(P*N*R)/100;
		System.out.println("The simple interest charged for Rs. 6000000 is "+SI);

	}

}
